package com.ufgov.zc.server.commonbiz.service.impl;import java.util.List;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.server.commonbiz.dao.IFundDao;import com.ufgov.zc.server.commonbiz.service.IFundService;public class FundService implements IFundService {  private IFundDao fundDao;  public IFundDao getFundDao() {    return fundDao;  }  public void setFundDao(IFundDao fundDao) {    this.fundDao = fundDao;  }  public List getFund(ElementConditionDto dto) {    return fundDao.getFund(dto);  }  public List getBiXJFund(ElementConditionDto dto) {    return fundDao.getBiXJFund(dto);  }  public void saveBiFundXJ(List insertList, List updateList) {    this.fundDao.updateBiFund(updateList);    this.fundDao.insertBiFund(insertList);  }  public void deleteFund(List idList) {    this.fundDao.deleteFund(idList);  }}