package com.ufgov.zc.server.cp.dao.ibatis;import java.sql.SQLException;import java.util.List;import org.springframework.orm.ibatis.SqlMapClientCallback;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ibatis.sqlmap.client.SqlMapExecutor;import com.ufgov.zc.common.cp.model.CpPayClearRelation;import com.ufgov.zc.server.cp.dao.ICpPayClearRelationDao;public class CpPayClearRelationDao extends SqlMapClientDaoSupport implements ICpPayClearRelationDao {  public List getCpPayClearRelationByBillId(String payClearBillId) {    return this.getSqlMapClientTemplate().queryForList("CpPayClearRelation.getCpPayClearRelationByBillId",    payClearBillId);  }  public void insertCpPayClearRelation(CpPayClearRelation cpPayClearRelation) {    this.getSqlMapClientTemplate().insert("CpPayClearRelation.insertCpPayClearRelation", cpPayClearRelation);  }  public void insertCpPayClearRelations(final List cpPayClearRelations) {    this.getSqlMapClientTemplate().execute(new SqlMapClientCallback() {      public Object doInSqlMapClient(SqlMapExecutor executor) throws SQLException {        executor.startBatch();        for (int i = 0; i < cpPayClearRelations.size(); i++) {          executor.insert("CpPayClearRelation.insertCpPayClearRelation", cpPayClearRelations.get(i));        }        executor.executeBatch();        return null;      }    });  }}