package com.ufgov.zc.server.cp.dao.ibatis;import java.sql.SQLException;import java.util.List;import org.springframework.orm.ibatis.SqlMapClientCallback;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ibatis.sqlmap.client.SqlMapExecutor;import com.ufgov.zc.common.cp.model.CpPlanAgentRelation;import com.ufgov.zc.server.cp.dao.ICpPlanAgentRelationDao;public class CpPlanAgentRelationDao extends SqlMapClientDaoSupport implements ICpPlanAgentRelationDao {  public List getCpPlanAgentRelation(String planAgentBillId) {    return this.getSqlMapClientTemplate().queryForList("CpPlanAgentRelation.getCpPlanAgentRelation", planAgentBillId);  }  public List getCpPlanAgentRelationForVouNo(String vouNo) {    return this.getSqlMapClientTemplate().queryForList("CpPlanAgentRelation.getCpPlanAgentRelationForVouNo", vouNo);  }  public void insertCpPlanAgentRelation(final List cpPlanAgentRelationList) {    this.getSqlMapClientTemplate().execute(new SqlMapClientCallback() {      public Object doInSqlMapClient(SqlMapExecutor executor) throws SQLException {        executor.startBatch();        for (int i = 0; i < cpPlanAgentRelationList.size(); i++) {          executor.insert("CpPlanAgentRelation.insert", cpPlanAgentRelationList.get(i));        }        executor.executeBatch();        return null;      }    });  }}