package com.ufgov.zc.server.cp.dao.ibatis;import java.sql.SQLException;import java.util.HashMap;import java.util.List;import java.util.Map;import org.springframework.orm.ibatis.SqlMapClientCallback;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ibatis.sqlmap.client.SqlMapExecutor;import com.ufgov.zc.common.system.exception.DaoException;import com.ufgov.zc.server.cp.dao.ICpPlanClearListDao;public class CpPlanClearListDao extends SqlMapClientDaoSupport implements ICpPlanClearListDao {  public List getCpPlanClearList(String planClearBillId) {    return this.getSqlMapClientTemplate().queryForList("CpPlanClearList.getCpPlanClearList", planClearBillId);  }  public List getCpPlanClearList(String planClearBillId, String billType) {    Map map = new HashMap();    map.put("planClearBillId", planClearBillId);    map.put("billType", billType);    return this.getSqlMapClientTemplate().queryForList("CpPlanClearList.getCpPlanClearListByBillType", map);  }  public List getCpPlanClearListForList(List planClearBillIdList) {    if (planClearBillIdList.size() == 0) {      throw new DaoException("getCpPlanClearListForList的参数planClearBillIdList为空");    }    Map map = new HashMap();    map.put("planClearBillIdList", planClearBillIdList);    return this.getSqlMapClientTemplate().queryForList("CpPlanClearList.getCpPlanClearListForList", map);  }  public void insertCpPlanClearLists(final List cpPlanClearLists) {    this.getSqlMapClientTemplate().execute(new SqlMapClientCallback() {      public Object doInSqlMapClient(SqlMapExecutor executor) throws SQLException {        executor.startBatch();        for (int i = 0; i < cpPlanClearLists.size(); i++) {          executor.insert("CpPlanClearList.abatorgenerated_insert", cpPlanClearLists.get(i));        }        executor.executeBatch();        return null;      }    });  }}