package com.ufgov.zc.server.cp.dao.ibatis;import java.sql.SQLException;import java.util.List;import org.springframework.orm.ibatis.SqlMapClientCallback;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ibatis.sqlmap.client.SqlMapExecutor;import com.ufgov.zc.server.cp.dao.ICpPlanClearRelationDao;public class CpPlanClearRelationDao extends SqlMapClientDaoSupport implements ICpPlanClearRelationDao {  public List getCpPlanClearRelation(String planClearBillId) {    return this.getSqlMapClientTemplate().queryForList("CpPlanClearRelation.getCpPlanClearRelation", planClearBillId);  }  public List getCpPlanClearRelationForVouNo(String vouNo) {    return this.getSqlMapClientTemplate().queryForList("CpPlanClearRelation.getCpPlanClearRelationForVouNo", vouNo);  }  public void insertCpPlanClearRelations(final List cpPlanClearRelations) {    this.getSqlMapClientTemplate().execute(new SqlMapClientCallback() {      public Object doInSqlMapClient(SqlMapExecutor executor) throws SQLException {        executor.startBatch();        for (int i = 0; i < cpPlanClearRelations.size(); i++) {          executor.insert("CpPlanClearRelation.insert", cpPlanClearRelations.get(i));        }        executor.executeBatch();        return null;      }    });  }}