package com.ufgov.zc.server.zc;import java.io.BufferedReader;import java.io.File;import java.io.FileReader;public class FileOperator {  public static void renameFile(String oldName, String newName) {    File file = new File(oldName);    File newFile = new File(newName);    file.renameTo(newFile);  }  public static String readFileContent(String filePath) throws Exception {    if (filePath == null || "".equals(filePath)) {      return "";    }    StringBuffer buff = new StringBuffer();    File file = new File(filePath);    FileReader fr = new FileReader(file);    BufferedReader br = new BufferedReader(fr);    String tmp = null;    while ((tmp = br.readLine()) != null) {      buff.append(tmp);      buff.append(System.getProperty("line.separator"));    }    return buff.toString();  }}