package com.ufgov.zc.server.zc.dao.ibatis;import java.util.List;import java.util.Map;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.zc.model.ApArticle;import com.ufgov.zc.server.zc.dao.IApArticleDao;public class ApArticleDao extends SqlMapClientDaoSupport implements IApArticleDao {  public String insertAutoKey(ApArticle record) {    Object newKey = getSqlMapClientTemplate().insert("AP_ARTICLE.insertAutoKey", record);    return (String) newKey;  }  public void insertApArticleWithID(ApArticle record) {    getSqlMapClientTemplate().insert("AP_ARTICLE.insertApArticleWithID", record);  }  public void insertArticlePortlet(Map map) {    getSqlMapClientTemplate().insert("AP_ARTICLE.insertArticlePortlet", map);  }  public ApArticle getArticleByZcMakeCode(ElementConditionDto dto) {    List list = this.getSqlMapClientTemplate().queryForList("AP_ARTICLE.selectApArticle", dto);    if (list.size() > 0) {      return (ApArticle) list.get(list.size() - 1);    }    return null;  }  public List getApArticlePortletByArticleID(ElementConditionDto dto) {    return this.getSqlMapClientTemplate().queryForList("AP_ARTICLE.selectApArticleProtlet", dto);  }  public void deleteApArticlePortlet(Map para) {    this.getSqlMapClientTemplate().delete("", para);  }  public void deleteApArticleWithID(Map para1) {  }  public ApArticle getArticleByArticleID(ElementConditionDto dto) {    return (ApArticle) this.getSqlMapClientTemplate().queryForObject("AP_ARTICLE.selectApArticle", dto);  }}