package com.ufgov.zc.server.zc.dao.ibatis;import java.util.List;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.zc.model.ZcEbPlan;import com.ufgov.zc.server.zc.dao.IZcEbPlanDao;public class ZcEbPlanDao extends SqlMapClientDaoSupport implements IZcEbPlanDao {  public ZcEbPlanDao() {    super();  }  public void insertZcEbPlan(final ZcEbPlan record) {    getSqlMapClientTemplate().insert("ZcEbPlan.insertZcEbPlan", record);  }  public void updateZcEbPlan(final ZcEbPlan record) {    getSqlMapClientTemplate().update("ZcEbPlan.updateZcEbPlan", record);  }  public List getZcEbPlan(ElementConditionDto elementConditionDto) {    return getSqlMapClientTemplate().queryForList("ZcEbPlan.getZcEbPlan", elementConditionDto);  }  public boolean deletePlan(ZcEbPlan plan) {    getSqlMapClientTemplate().delete("ZcEbPlan.deleteZcEbPlan", plan.getPlanCode());    return true;  }  public ZcEbPlan getZcEbPlanByID(String planCode) {    return (ZcEbPlan) getSqlMapClientTemplate().queryForObject("ZcEbPlan.getZcEbPlanByID", planCode);  }  public ZcEbPlan getObjZcEbPlanByProjCode(String projCode) {    return (ZcEbPlan) getSqlMapClientTemplate().queryForObject("ZcEbPlan.getZcEbPlanByProjCode", projCode);  }  public List getZcEbPlanByProjCode(String projcode) {    ElementConditionDto dto = new ElementConditionDto();    dto.setProjCode(projcode);    return getZcEbPlan(dto);  }  public void deletePlanByProjcode(String projCode) {    getSqlMapClientTemplate().delete("ZcEbPlan.deleteZcEbPlanByProjCode", projCode);  }  public ZcEbPlan getHistoryZcEbPlan(ElementConditionDto dto) {    return (ZcEbPlan) this.getSqlMapClientTemplate().queryForObject("ZcEbPlan.getHistoryZcEbPlan", dto);  }}