package com.ufgov.zc.server.zc.dao.ibatis;import java.util.List;import org.springframework.orm.ibatis.support.SqlMapClientDaoSupport;import com.ufgov.zc.common.console.model.AsEmp;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.zc.model.ZcEbPlan;import com.ufgov.zc.common.zc.model.ZcEbPresent;import com.ufgov.zc.server.zc.dao.IZcEbPresentDao;public class ZcEbPresentDaoImpl extends SqlMapClientDaoSupport implements IZcEbPresentDao {  public void delete(ZcEbPresent present, RequestMeta requestMeta) {    this.getSqlMapClientTemplate().delete("ZcEbPresent.deletePresent", present);  }  public List getPresentList(ElementConditionDto elementConditionDto, RequestMeta requestMeta) {    return this.getSqlMapClientTemplate().queryForList("ZcEbPresent.getPresentList", elementConditionDto);  }  public ZcEbPresent insert(ZcEbPresent present, RequestMeta requestMeta) {    this.getSqlMapClientTemplate().insert("ZcEbPresent.insertPresent", present);    String sn = (String) this.getSqlMapClientTemplate().queryForObject("ZcEbPresent.getSn");    return (ZcEbPresent) this.getSqlMapClientTemplate().queryForObject("ZcEbPresent.getPresentById", sn);  }  public ZcEbPresent update(ZcEbPresent present, RequestMeta requestMeta) {    this.getSqlMapClientTemplate().update("ZcEbPresent.updatePresent", present);    return getZcEbPresentById(present);  }  public AsEmp getComplany(ElementConditionDto elementConditionDto, RequestMeta requestMeta) {    return (AsEmp) this.getSqlMapClientTemplate().queryForObject("ZcEbPresent.getComplany", elementConditionDto.getProjCode());  }  public List getSupplierList(ElementConditionDto elementConditionDto, RequestMeta requestMeta) {    return this.getSqlMapClientTemplate().queryForList("ZcEbPresent.getSupplier", elementConditionDto.getProjCode());  }  public List getZcEbOpenBidTeamMemberList(ElementConditionDto elementConditionDto, RequestMeta requestMeta) {    return this.getSqlMapClientTemplate().queryForList("ZcEbPresent.getOpenBidTeamMember", elementConditionDto.getProjCode());  }  public ZcEbPlan getZcEbPlan(ElementConditionDto elementConditionDto, RequestMeta requestMeta) {    return (ZcEbPlan) this.getSqlMapClientTemplate().queryForObject("ZcEbPresent.", elementConditionDto.getProjCode());  }  public ZcEbPresent getZcEbPresentById(ZcEbPresent present) {    return (ZcEbPresent) this.getSqlMapClientTemplate().queryForObject("ZcEbPresent.getPresentById", present.getSn());  }}