package com.ufgov.zc.server.zc.expertQA;import java.util.ArrayList;import java.util.List;import java.util.Map;public class DoMethod {  private String object;  private String selfObjectName;  private String toObjectName;  private String time;  private String content;  private String flag;  private String projectCode;  private String packCode;  public DoMethod() {  }  public DoMethod(Map map) {    if (map.get("object") != null)      this.object = (String) map.get("object");    if (map.get("selfObjectName") != null)      this.selfObjectName = (String) map.get("selfObjectName");    if (map.get("toObjectName") != null)      this.toObjectName = (String) map.get("toObjectName");    if (map.get("time") != null)      this.time = (String) map.get("time");    if (map.get("content") != null)      this.content = (String) map.get("content");    if (map.get("projectCode") != null)      this.projectCode = (String) map.get("projectCode");    if (map.get("packCode") != null)      this.packCode = (String) map.get("packCode");    if ("supplier".equals(object)) {      flag = toObjectName + selfObjectName;    } else if ("expert".equals(object)) {      flag = selfObjectName + toObjectName;    }  }  public void startOperation() {    QAContent qaContent = QAContent.getInstance(flag, object, selfObjectName, toObjectName, time, content, projectCode, packCode);    qaContent.setModel(object, selfObjectName, toObjectName, time, content, projectCode, packCode);  }  public List getContent() {    List list = new ArrayList();    QAContent qaContent = QAContent.getInstance(flag);    if (qaContent != null) {      list = qaContent.getContent();    } else {      list = null;    }    return list;  }  public void destroy(String flag) {    QAContent.destroy(flag);  }}