package com.ufgov.zc.server.zc.fileResumeBroken.authentication;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.zc.model.ZcEbSignup;import com.ufgov.zc.server.system.SpringContext;import com.ufgov.zc.server.zc.service.IZcEbSignupService;public class ProjectRegistrationVerify {  private String projectId;  private String providerId;  private boolean ifPass;  private String packId;  private String failReason;  public String getFailReason() {    return failReason;  }  public void setFailReason(String failReason) {    this.failReason = failReason;  }  private IZcEbSignupService zcEbSignupService;  public String getPackId() {    return packId;  }  public void setPackId(String packId) {    this.packId = packId;  }  public String getProviderId() {    return providerId;  }  public void setProviderId(String providerId) {    this.providerId = providerId;  }  public String getProjectId() {    return projectId;  }  public void setProjectId(String projectId) {    this.projectId = projectId;  }  public boolean isIfPass() {    return ifPass;  }  public void setIfPass(boolean ifPass) {    this.ifPass = ifPass;  }  public ProjectRegistrationVerify(String projectId, String providerId, String packId) {    setProjectId(projectId);    setProviderId(providerId);    setPackId(packId);  }  public void start() {    ElementConditionDto dto = new ElementConditionDto();    dto.setProjCode(getProjectId());    dto.setExtField1(getProviderId());    dto.setExtField2(getPackId());    ZcEbSignup zcEbSignup = null;    try {      zcEbSignupService = (IZcEbSignupService) SpringContext.getBean("zcEbSignupService");      zcEbSignup = zcEbSignupService.getZcEbSignupByInfo(dto);    } catch (Exception e) {      String info = "供应商项目报名查询出错...pjID:" + getProjectId() + ",pkID:" + getPackId() + ",pdID:" + getProviderId();      System.out.println(info);      e.printStackTrace();      this.setFailReason(info + ",异常内容：" + e.getMessage());    }    if (zcEbSignup != null) {      setIfPass(true);    } else {      this.setFailReason("根据项目编号:" + projectId + ",分包编号:" + packId + ",供应商号:" + providerId + "没有找到报名信息。");      setIfPass(false);    }  }}