package com.ufgov.zc.server.zc.fileResumeBroken.serverFileOperate;import java.util.HashMap;public class IfFileTransSuccess {  private static HashMap hm = new HashMap();  private String flag;  private int localFileTotalLen = 0;  private int serverFileTotalLen = 0;  private IfFileTransSuccess() {  }  private IfFileTransSuccess(String flag) {    this.flag = flag;  }  public static IfFileTransSuccess getInstance(String flag) {    if (flag != null && !"".equals(flag)) {      if (hm.get(flag) == null)        hm.put(flag, new IfFileTransSuccess(flag));    }    return (IfFileTransSuccess) hm.get(flag);  }  public String getFlag() {    return flag;  }  public void setFlag(String flag) {    this.flag = flag;  }  public int getLocalFileTotalLen() {    return localFileTotalLen;  }  public void setLocalFileTotalLen(int localFileTotalLen) {    this.localFileTotalLen = localFileTotalLen;  }  public int getServerFileTotalLen() {    return serverFileTotalLen;  }  public void setServerFileTotalLen(int serverFileTotalLen) {    this.serverFileTotalLen = serverFileTotalLen;  }}