package com.ufgov.zc.server.zc.service.impl;import java.util.List;import java.util.Map;import com.ufgov.zc.common.zc.model.ZcBBrand;import com.ufgov.zc.common.zc.model.ZcBMerchandise;import com.ufgov.zc.server.zc.dao.IZcBrandDao;import com.ufgov.zc.server.zc.service.IZcBrandService;public class ZcBrandService implements IZcBrandService {  private IZcBrandDao zcBrandDao;  public IZcBrandDao getZcBrandDao() {    return zcBrandDao;  }  public void setZcBrandDao(IZcBrandDao zcBrandDao) {    this.zcBrandDao = zcBrandDao;  }  public ZcBBrand getBrandInfoByBrandId(String brandId) {    return zcBrandDao.getBrandInfoByBrandId(brandId);  }  public ZcBBrand getBrandInfoByBrandId(ZcBMerchandise zcBMerchandise) {    return zcBrandDao.getBrandInfoByBrandId(zcBMerchandise);  }  public List getBrandsForMenuGoods(String catalogueId) {    return zcBrandDao.getBrandsForMenuGoods(catalogueId);  }  public List getBrandsForMenuGoods(Map parameterObject) {    return zcBrandDao.getBrandsForMenuGoods(parameterObject);  }}