package com.ufgov.zc.server.zc.service.impl;import java.util.HashMap;import java.util.List;import java.util.Map;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.dto.ElementConditionDto;import com.ufgov.zc.common.zc.model.EmExpertType;import com.ufgov.zc.server.zc.dao.IBaseDao;import com.ufgov.zc.server.zc.service.IZcEmExpertTypeService;public class ZcEmExpertTypeService implements IZcEmExpertTypeService {  private IBaseDao baseDao;  public List getList(ElementConditionDto dto, RequestMeta requestMeta) {    return baseDao.query("EmExpertType.list", dto);  }  public EmExpertType getModel(Map map, RequestMeta requestMeta) {    return (EmExpertType) baseDao.read("EmExpertType.read", map);  }  public EmExpertType save(EmExpertType o, RequestMeta requestMeta) {    if (o.getEmTypeCode().equals("自动编号")) {      Map queryMap = new HashMap();      if (o.getEmParentTypeCode() == null || "".equals(o.getEmParentTypeCode().trim())) {        queryMap.put("PARENT_CODE", "ET");      } else {        queryMap.put("PARENT_CODE", o.getEmParentTypeCode());      }      String rlt = (String) baseDao.read("EmExpertType.maxOfThisLevel", queryMap);      o.setEmTypeCode(rlt);      baseDao.insert("EmExpertType.insert", o);    } else {      baseDao.update("EmExpertType.update", o);    }    return o;  }  public void delete(Map map, RequestMeta requestMeta) {    baseDao.delete("EmExpertType.delete", map);    baseDao.delete("EmExpertType.deleteEmExpertTypeJion", map);  }  public IBaseDao getBaseDao() {    return baseDao;  }  public void setBaseDao(IBaseDao baseDao) {    this.baseDao = baseDao;  }}