package com.ufgov.zc.server.zc.util;import java.io.IOException;import java.util.HashMap;import java.util.Map;import com.ufgov.zc.common.system.model.User;import freemarker.template.Configuration;import freemarker.template.DefaultObjectWrapper;import freemarker.template.Template;import freemarker.template.TemplateExceptionHandler;public class FreeMarkerTemplateUtils {  private final static String Template_Dir = "gongGaoTpl";  private final static Configuration config = new Configuration();  protected Configuration getConfiguration() {    config.setClassForTemplateLoading(this.getClass(), Template_Dir);    config.setObjectWrapper(new DefaultObjectWrapper());    config.setTemplateExceptionHandler(TemplateExceptionHandler.IGNORE_HANDLER);    return config;  }  public Template getTemplate(String templateName) {    try {      return getConfiguration().getTemplate(templateName);    } catch (IOException e) {      throw new RuntimeException(e);    }  }  public static String process(Template template, Object model) {    try {      return org.springframework.ui.freemarker.FreeMarkerTemplateUtils.processTemplateIntoString(template, model);    } catch (Exception e) {      throw new RuntimeException(e);    }  }  public String process(String templateName, Object model) {    return process(getTemplate(templateName), model);  }  public static void main(String[] args) {    Map map = new HashMap();    User user = new User();    user.setUserId("11111111111111");    user.setUserName("22222222222222");    map.put("user", user);    FreeMarkerTemplateUtils utils = new FreeMarkerTemplateUtils();    String content = utils.process("test.ftl", map);    System.out.println(content);  }}