package com.ufgov.zc.server.zc.web.action.menugoods;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;import org.apache.struts.action.ActionForm;import org.apache.struts.action.ActionForward;import org.apache.struts.action.ActionMapping;import com.ufgov.zc.server.zc.service.IMenuGoodsService;import com.ufgov.zc.server.zc.web.action.StrutsAction;import com.ufgov.zc.server.zc.web.form.MerchandiseForm;public class SpMerAddSaveAction extends StrutsAction {  private final static Log log = LogFactory.getLog(SpMerAddSaveAction.class);  public ActionForward execute(ActionMapping mapping, ActionForm form, HttpServletRequest request, HttpServletResponse response) throws Exception {    String forward = "success";    MerchandiseForm merchandiseForm = (MerchandiseForm) form;    IMenuGoodsService menuGoodsService = (IMenuGoodsService) this.getWebApplicationContext().getBean("menuGoodsService");    menuGoodsService.addMerchandise(merchandiseForm);    return mapping.findForward(forward);  }}