package com.ufgov.zc.server.zc.web.form;import com.ufgov.zc.common.zc.model.ZcHtPrePayBill;import com.ufgov.zc.server.system.SpringContext;import com.ufgov.zc.server.zc.service.IZcHtPrePayBillService;public class ZcHtPrePayBillPrintForm extends AbstractActionForm {	private ZcHtPrePayBill bill;	public ZcHtPrePayBill getBill() {		return bill;	}	public void setBill(ZcHtPrePayBill bill) {		this.bill = bill;	}	private IZcHtPrePayBillService zcHtPrePayBillService;	public String toPrePayBillPrint() {		String billCode = this.getRequest().getParameter("billCode");		zcHtPrePayBillService = (IZcHtPrePayBillService)SpringContext.getBean("zcHtPrePayBillService");		bill = zcHtPrePayBillService.selectByPrimaryKey(billCode);		this.getRequest().setAttribute("bill", bill);		return SUCCESS;	}}