package com.ufgov.zc.client.component;import java.awt.Dialog;import java.awt.event.MouseAdapter;import java.awt.event.MouseEvent;import java.util.List;import javax.swing.JTable;import javax.swing.table.DefaultTableModel;import com.ufgov.zc.client.common.ServiceFactory;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.client.util.GkPreferencesStore;import com.ufgov.zc.common.commonbiz.model.BankAccount;import com.ufgov.zc.common.commonbiz.publish.IBaseDataServiceDelegate;import com.ufgov.zc.common.system.RequestMeta;public class PayBankAccountSelectDialog extends JTableSelectDialog {  public PayBankAccountSelectDialog(Dialog dialog, boolean modal, JButtonTextField triggerField) {    super(dialog, modal, triggerField);  }  public void initTitle() {    this.setTitle("付款人");  }  protected void initDataBufferList() {    String baseDataServiceDelegateName = "baseDataServiceDelegate";    IBaseDataServiceDelegate baseDataServiceDelegate = (IBaseDataServiceDelegate) ServiceFactory.create(    IBaseDataServiceDelegate.class, baseDataServiceDelegateName);    int nd = WorkEnv.getInstance().getTransNd();    RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();    dataBufferList = baseDataServiceDelegate.getPayBankAccount(nd, null, null, requestMeta);    for (int i = 0; i < dataBufferList.size(); i++) {      BankAccount rowData = (BankAccount) dataBufferList.get(i);      this.triggerField.getDataMap().put(rowData.getBankAccCode(), rowData);    }    this.triggerField.filteredDataList = dataBufferList;  }  private List genTableData() {    PayBankAccountSelectField PayBankAccountField = (PayBankAccountSelectField) triggerField;    List list = PayBankAccountFilter.getPayBankAccount(PayBankAccountField.getFund(), dataBufferList);    return list;  }  public void initSelectTable() {    String[] names = { "全称", "银行账号", "开户银行" };    Object[][] data = null;    tableDataList = this.genTableData();    data = new Object[tableDataList.size()][names.length];    for (int i = 0; i < tableDataList.size(); i++) {      BankAccount rowData = (BankAccount) tableDataList.get(i);      int col = 0;      data[i][col++] = rowData.getAccName();      data[i][col++] = rowData.getBankAccCode();      data[i][col++] = rowData.getBankNodeName();    }    DefaultTableModel model = new DefaultTableModel(data, names) {      public boolean isCellEditable(int row, int colum) {        return false;      }    };    selectTable.setPreferencesKey(this.getClass().getName() + "_selectTable");    selectTable.setPreferenceStore(GkPreferencesStore.preferenceStore());    selectTable.setModel(model);    selectTable.addMouseListener(new MouseAdapter() {      public void mouseClicked(MouseEvent e) {        if (e.getClickCount() == 2) {          doOK();        }      }    });    selectTable.setAutoResizeMode(JTable.AUTO_RESIZE_OFF);  }}