package com.ufgov.zc.client.component.setting;import com.ufgov.zc.client.common.AsOptionMeta;import com.ufgov.zc.client.component.element.ColorSelectPanel;import com.ufgov.zc.common.system.constants.BusinessOptionConstants;public class SysColorPanelOption extends AbstractOption<ColorSelectPanel> {  private String colorString;  public SysColorPanelOption() {    this.init(BusinessOptionConstants.OPT_UNTREAD_COLOR_OPTION, "退回单据的颜色：");    String code = AsOptionMeta.getOptVal(optId);    this.oldValue = code;    optionEditor = new ColorSelectPanel(code);    colorString = (String) this.optionEditor.getColor();  }  public void updateOption() {    colorString = (String) this.optionEditor.getColor();    String code = "";    if (colorString != null) {      code = colorString;    }    if (!code.equals(oldValue)) {      AsOptionMeta.updateOptVal(optId, code);      oldValue = code;    }  }  @Override  protected ColorSelectPanel createOptionEditor() {    return optionEditor;  }  public String getColorString() {    return optionEditor.getColor();  }}