package com.ufgov.zc.client.component.setting;import com.ufgov.smartclient.component.JComboBoxEx;import com.ufgov.zc.client.common.AsOptionMeta;import com.ufgov.zc.common.system.constants.SystemOptionConstants;public class SysFontSizeComboBoxOption extends AbstractOption<JComboBoxEx> {  /**   *    */  private static final long serialVersionUID = 1L;  public SysFontSizeComboBoxOption() {    this.init(SystemOptionConstants.OPT_PREFERRED_FONT_SIZE, "字体大小：");  }  protected JComboBoxEx createOptionEditor() {    String value = AsOptionMeta.getOptVal(optId);    this.oldValue = value;    final JComboBoxEx editor = new JComboBoxEx();    for (int i = 12; i <= 20; i++) {      editor.addItem(i + "");    }    editor.setSelectedItem(value);    return editor;  }  public void updateOption() {    String size = (String) this.optionEditor.getSelectedItem();    if (!size.equals(oldValue)) {      AsOptionMeta.updateOptVal(optId, size);      oldValue = size;    }  }}