package com.ufgov.zc.client.component.setting;import java.awt.BorderLayout;import java.awt.Container;import java.awt.Dialog;import java.awt.Window;import javax.swing.JTabbedPane;import com.ufgov.zc.client.component.GkBaseDialog;import com.ufgov.zc.client.component.setting.cp.CpOptionTab;public class SysOptionDialog extends GkBaseDialog {  private JTabbedPane tabPane = new JTabbedPane();  public SysOptionDialog(Window window) {    super(window, Dialog.ModalityType.APPLICATION_MODAL);    setTitle("系统选项");    init();    this.setSize(660, 500);    this.setVisible(true);  }  public void init() {    Container container = this.getContentPane();    container.setLayout(new BorderLayout());    OptionTab ot = new SysOptionTab(this);    tabPane.addTab(ot.getTabName(), ot);    ot = new CpOptionTab(this);    tabPane.addTab(ot.getTabName(), ot);    container.add(tabPane, BorderLayout.CENTER);  }  public static void main(String[] args) {    new SysOptionDialog(null);  }}