package com.ufgov.zc.client.component.setting.cp;import com.ufgov.smartclient.component.JComboBoxEx;import com.ufgov.zc.client.common.AsOptionMeta;import com.ufgov.zc.client.component.BalModeComboBox;import com.ufgov.zc.client.component.setting.AbstractOption;import com.ufgov.zc.common.commonbiz.model.BalMode;import com.ufgov.zc.common.system.constants.BusinessOptionConstants;public class CpBalModeComboBoxOption extends AbstractOption<JComboBoxEx> {  /**   *   */  private static final long serialVersionUID = 1L;  public CpBalModeComboBoxOption() {    this.init(BusinessOptionConstants.OPT_CP_CP_AV_MOENY_BAL_MODE, "现金的结算方式代码：");  }  protected JComboBoxEx createOptionEditor() {    String code = AsOptionMeta.getOptVal(optId);    this.oldValue = code;    final BalModeComboBox editor = new BalModeComboBox();    editor.setSelectedBalModeByCode(code);    return editor;  }  public void updateOption() {    BalMode balMode = (BalMode) this.optionEditor.getSelectedItem();    String code = "";    if (balMode != null) {      code = balMode.getBalModeCode();    }    if (!code.equals(oldValue)) {      AsOptionMeta.updateOptVal(optId, code);      oldValue = code;    }  }}