package com.ufgov.zc.client.component.setting.cp;import javax.swing.JTextField;import com.ufgov.zc.client.component.MoneyDocument;import com.ufgov.zc.client.component.setting.TextFieldOption;import com.ufgov.zc.common.system.constants.BusinessOptionConstants;public class CpDayUseMoneyOption extends TextFieldOption {  public CpDayUseMoneyOption() {    this.init(BusinessOptionConstants.OPT_CP_VOUCHER_DAY_MONEY, "授权支付凭证日用金额：");  }  protected JTextField createOptionEditor() {    JTextField field = super.createOptionEditor();    String v = field.getText();    field.setDocument(new MoneyDocument(false));    field.setText(v);    return field;  }}