package com.ufgov.zc.client.component.setting.cp;import com.ufgov.smartclient.component.JComboBoxEx;import com.ufgov.zc.client.common.AsOptionMeta;import com.ufgov.zc.client.component.OperationTypeComboBox;import com.ufgov.zc.client.component.setting.AbstractOption;import com.ufgov.zc.common.commonbiz.model.OperationType;public class CpOperationTypeComboBoxOption extends AbstractOption<JComboBoxEx> {  /**   *   */  private static final long serialVersionUID = 1L;  public CpOperationTypeComboBoxOption(String optId, String optName) {    this.init(optId, optName);  }  protected JComboBoxEx createOptionEditor() {    String code = AsOptionMeta.getOptVal(optId);    this.oldValue = code;    final OperationTypeComboBox editor = new OperationTypeComboBox();    editor.initComboBox();    editor.setSelectedOperationTypeByCode(code);    return editor;  }  public void updateOption() {    OperationType operationType = (OperationType) this.optionEditor.getSelectedItem();    String code = "";    if (operationType != null) {      code = operationType.getCode();    }    if (!code.equals(oldValue)) {      AsOptionMeta.updateOptVal(optId, code);      oldValue = code;    }  }}