package com.ufgov.zc.client.component.setting.jj;import java.awt.Window;import java.util.ArrayList;import java.util.List;import com.ufgov.zc.client.component.setting.AbstractOption;import com.ufgov.zc.client.component.setting.OptionTab;public class JjOptionTab extends OptionTab {  private Window owner;  public JjOptionTab(Window owner) {    this.owner = owner;    List<AbstractOption> optionList = new ArrayList<AbstractOption>();    AbstractOption ao = new JjFundOption();    optionList.add(ao);    this.init("JjOptionTab", "基建系统的系统选项", optionList);  }  @Override  protected void doCancel() {    owner.dispose();  }}