package com.ufgov.zc.client.component.table.celleditor;import java.awt.Component;import java.util.List;import javax.swing.AbstractCellEditor;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.component.PayBankAccountSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;public class PayBankCellEditor extends AbstractCellEditor implements TableCellEditor {  PayBankAccountSelectField payBankField;  PayBankCellEditor self = this;  public PayBankCellEditor() {    payBankField = new PayBankAccountSelectField(20);    payBankField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        self.stopCellEditing();      }    });  }  public PayBankCellEditor(List payBankAccountList) {    payBankField = new PayBankAccountSelectField(20, payBankAccountList);    payBankField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        self.stopCellEditing();      }    });  }  public Component getTableCellEditorComponent(final JTable table, Object value, boolean isSelected, int row,  int column) {    payBankField.setValueByCode((String) value);    return payBankField;  }  public Object getCellEditorValue() {    Object obj = payBankField.getBankAccount();    if (obj != null) {      return payBankField.getBankAccount();    } else {      return payBankField.getText();    }  }}