package com.ufgov.zc.client.component.table.cellrenderer;import java.awt.Component;import java.util.Date;import javax.swing.JTable;import com.ufgov.smartclient.component.table.TableCellDisplayValueProvider;import com.ufgov.zc.common.system.util.DateUtil;public class DateCellRenderer extends LineWrapCellRenderer implements TableCellDisplayValueProvider {  /**   *    */  private static final long serialVersionUID = 3184261311169929819L;  public static final String CH = "china";  public static final String SS = "ss";  public static final String DD = "dd";  private String type = DD;  public DateCellRenderer() {    super();  }  public DateCellRenderer(String type) {    super();    if (CH.equals(type) || SS.equals(type))      this.type = type;  }  public Component getTableCellRendererComponent(JTable table, Object value, boolean isSelected, boolean hasFocus, int row, int column) {    String dateString = "";    if (value instanceof java.util.Date) {      if (CH.equals(type)) {        dateString = DateUtil.dateToChinaString((Date) value);      } else if (SS.equals(type)) {        dateString = DateUtil.dateToSsString((Date) value);      } else {        dateString = DateUtil.dateToDdString((Date) value);      }    }    return super.getTableCellRendererComponent(table, dateString, isSelected, hasFocus, row, column);  }  @Override  public Object getDisplayValue(JTable jtable, Object obj, int i, int j) {    if (obj instanceof java.util.Date) {      if (CH.equals(type)) {        return DateUtil.dateToChinaString((Date) obj);      } else if (SS.equals(type)) {        return DateUtil.dateToSsString((Date) obj);      } else {        return DateUtil.dateToDdString((Date) obj);      }    }    return "";  }}