package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.component.BalModeComboBox;import com.ufgov.zc.client.component.table.GkAbstractCellEditor;import com.ufgov.zc.common.commonbiz.model.BalMode;public class BalModeCellEditor extends GkAbstractCellEditor implementsTableCellEditor {  private static final long serialVersionUID = -1014964575090820890L;  private BalModeComboBox editor = new BalModeComboBox();  private BalModeCellEditor self = this;  public BalModeCellEditor() {    editor.addActionListener(new ActionListener() {      public void actionPerformed(ActionEvent e) {        self.stopCellEditing();      }    });  }  public Component getTableCellEditorComponent(JTable table, Object value,  boolean isSelected, int row, int column) {    editor.setSelectedBalModeByCode((String) value);    return editor;  }  public Object getCellEditorValue() {    String code = null;    BalMode value = editor.getSelectedBalMode();    if (value != null) {      code = value.getBalModeCode();    }    return code;  }}