package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import java.util.List;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.client.component.PayBankAccountSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.client.component.table.GkAbstractCellEditor;import com.ufgov.zc.client.datacache.CzZeroAccountDataCache;public class CzZeroAccountCellEditor2 extends GkAbstractCellEditor implements TableCellEditor {  private static final long serialVersionUID = 1L;  PayBankAccountSelectField payBankField;  CzZeroAccountCellEditor2 self = this;  public CzZeroAccountCellEditor2() {    String coCode = WorkEnv.getInstance().getCurrCoCode();    List payBankAccountList = CzZeroAccountDataCache.getCzZeroAccountList(coCode);    payBankField = new PayBankAccountSelectField(20, payBankAccountList);    payBankField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        self.stopCellEditing();      }    });  }  public Component getTableCellEditorComponent(final JTable table, Object value, boolean isSelected, int row,  int column) {    payBankField.setValueByCode((String) value);    return payBankField;  }  public Object getCellEditorValue() {    return payBankField.getBankAccount();  }}