package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.component.element.DAttrTableSelectField;import com.ufgov.zc.client.component.table.GkAbstractCellEditor;import com.ufgov.zc.common.commonbiz.model.DAttr;public class DAttrTableSelectCellEditor extends GkAbstractCellEditor implementsTableCellEditor {  private static final long serialVersionUID = -1014964575090820890L;  private DAttrTableSelectField editorComponent;  private String dattrType;  private String compoId;  public DAttrTableSelectCellEditor(String dattrType) {    this.dattrType = dattrType;    this.init();  }  public DAttrTableSelectCellEditor(String dattrType, String compoId) {    this.dattrType = dattrType;    this.compoId = compoId;    this.init();  }  private void init() {    editorComponent = new DAttrTableSelectField(20, dattrType, compoId);  }  public Component getTableCellEditorComponent(JTable table, Object value,  boolean isSelected, int row, int column) {    editorComponent.setValueByCode((String) value);    return editorComponent;  }  public Object getCellEditorValue() {    String code = null;    DAttr value = editorComponent.getDAttr();    if (value != null) {      code = value.getCode();    }    return code;  }}