package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import javax.swing.AbstractCellEditor;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.component.element.FundForBiXJComboBox;import com.ufgov.zc.common.commonbiz.model.Fund;public class FundForBiXJComboBoxCellEditor extends AbstractCellEditor implements TableCellEditor {  private static final long serialVersionUID = 705539581226254667L;  private FundForBiXJComboBox editorComponent;  {    editorComponent = new FundForBiXJComboBox();    editorComponent.initComboBox();  }  public FundForBiXJComboBoxCellEditor() {  }  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row,  int column) {    editorComponent.setSelectedFundByCode((String) value);    return editorComponent;  }  public Object getCellEditorValue() {    String code = null;    Fund value = editorComponent.getSelectedFund();    if (value != null) {      code = value.getCode();    }    return code;  }}