package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import javax.swing.AbstractCellEditor;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.component.element.ManageForBiXJComboBox;import com.ufgov.zc.common.commonbiz.model.Manage;public class ManageForBiXJComboBoxCellEditor extends AbstractCellEditor implements TableCellEditor {  private static final long serialVersionUID = -6334857141084237145L;  private ManageForBiXJComboBox editorComponent;  {    editorComponent = new ManageForBiXJComboBox();    editorComponent.initComboBox();  }  public ManageForBiXJComboBoxCellEditor() {  }  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row,  int column) {    editorComponent.setSelectedManageByCode((String) value);    return editorComponent;  }  public Object getCellEditorValue() {    String code = null;    Manage value = editorComponent.getSelectedManage();    if (value != null) {      code = value.getCode();    }    return code;  }}