package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import javax.swing.AbstractCellEditor;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.common.WorkEnv;import com.ufgov.zc.client.component.element.BAccTreeSelectField;import com.ufgov.zc.common.commonbiz.model.BAcc;public class NextYearBAccCellEditor extends AbstractCellEditor implements TableCellEditor {  private static final long serialVersionUID = -1014964575090820890L;  private BAccTreeSelectField editorComponent = new BAccTreeSelectField(20, WorkEnv.getInstance().getTransNd() + 1);  public NextYearBAccCellEditor() {  }  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row,  int column) {    editorComponent.setValueByCode((String) value);    return editorComponent;  }  public Object getCellEditorValue() {    String code = null;    BAcc value = editorComponent.getBAcc();    if (value != null) {      code = value.getCode();    }    return code;  }}