package com.ufgov.zc.client.component.table.codecelleditor;import java.awt.Component;import javax.swing.JTable;import javax.swing.table.TableCellEditor;import com.ufgov.zc.client.common.BillElementMeta;import com.ufgov.zc.client.component.element.OrgComboBox;import com.ufgov.zc.client.component.table.GkAbstractCellEditor;import com.ufgov.zc.common.commonbiz.model.Org;public class OrgCellEditor extends GkAbstractCellEditor implements TableCellEditor {  private static final long serialVersionUID = -1014964575090820890L;  private OrgComboBox editorComponent = new OrgComboBox();  public OrgCellEditor() {    editorComponent.initComboBox();  }  public OrgCellEditor(BillElementMeta bem) {    editorComponent.setBillElementMeta(bem);  }  public Component getTableCellEditorComponent(JTable table, Object value, boolean isSelected, int row,  int column) {    editorComponent.setSelectedOrgByCode((String) value);    return editorComponent;  }  public Object getCellEditorValue() {    String code = null;    Org value = editorComponent.getSelectedOrg();    if (value != null) {      code = value.getCode();    }    return code;  }}