package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.BAccTreeSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.BAcc;import com.ufgov.zc.common.system.dto.ElementConditionDto;/** * 功能分类查找Item * @author longrm * */public class BAccTreeConditionItem extends AbstractSearchConditionItem {  private static final long serialVersionUID = -7395785099416302251L;  private BAccTreeSelectField selectField;  public BAccTreeConditionItem(String displayName) {    init(displayName);  }  public BAccTreeConditionItem(String displayName, Object defaultValue) {    init(displayName);    setValue(defaultValue);  }  @Override  protected JComponent createEditorComponent() {    if (selectField == null)      selectField = new BAccTreeSelectField(20);    selectField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        fireSearch();        fireValueChanged();      }    });    return selectField;  }  @Override  public Object getValue() {    return selectField.getValue();  }  @Override  public void setValue(Object value) {    selectField.setValue(value);  }  @Override  public void putToElementConditionDto(ElementConditionDto element) {    BAcc bacc = (BAcc) getValue();    element.setBaccCode(bacc == null ? null : bacc.getCode());  }}