package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.BAccTreeSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.BAcc;public class BAccTreeSelectFieldConditionItem extends AbstractSearchConditionItem {  public BAccTreeSelectFieldConditionItem(String displayName) {    init(displayName);  }  private BAccTreeSelectField bAccTreeSelectField;  @Override  protected JComponent createEditorComponent() {    if (bAccTreeSelectField == null) {      bAccTreeSelectField = new BAccTreeSelectField(20);      bAccTreeSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          BAcc value = bAccTreeSelectField.getBAcc();          String code = null;          if (value != null) {            code = value.getCode();          }          fireSearch();          fireValueChanged();        }      });    }    return bAccTreeSelectField;  }  @Override  public void setValue(Object value) {    bAccTreeSelectField.setBAcc((BAcc) value);  }  @Override  public Object getValue() {    return bAccTreeSelectField.getBAcc();  }}