package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.CompanyTreeSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Company;public class CompanyTreeFieldConditionItem extends AbstractSearchConditionItem {  private CompanyTreeSelectField companyTreeSelectField;  public CompanyTreeFieldConditionItem(String displayName) {    companyTreeSelectField = new CompanyTreeSelectField(20);    init(displayName);  }  public CompanyTreeFieldConditionItem(String displayName, String compoId) {    companyTreeSelectField = new CompanyTreeSelectField(20, compoId);    init(displayName);  }  @Override  protected JComponent createEditorComponent() {    companyTreeSelectField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        Company value = companyTreeSelectField.getCompany();        String code = null;        if (value != null) {          code = value.getCode();        }        fireSearch();        fireValueChanged();      }    });    return companyTreeSelectField;  }  @Override  public void setValue(Object value) {    companyTreeSelectField.setCompany((Company) value);  }  public void setValueByCode(String code) {    companyTreeSelectField.setValueByCode(code);  }  @Override  public Object getValue() {    return companyTreeSelectField.getCompany();  }}