package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.DAttrTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.DAttr;import com.ufgov.zc.common.system.dto.ElementConditionDto;public class DAttrTableSelectFieldConditionItem extends AbstractSearchConditionItem {  private DAttrTableSelectField editField;  private String dattrType;  private String compoId;  public DAttrTableSelectFieldConditionItem(String displayName, String dattrType) {    this.dattrType = dattrType;    init(displayName);  }  public DAttrTableSelectFieldConditionItem(String displayName, String dattrType, String compoId) {    this.dattrType = dattrType;    this.compoId = compoId;    init(displayName);  }  protected JComponent createEditorComponent() {    if (editField == null) {      editField = new DAttrTableSelectField(18, dattrType, compoId);      editField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return editField;  }  public Object getValue() {    return editField.getValue();  }  public void setValue(Object value) {    editField.setValue(value);  }  public void putToElementConditionDto(ElementConditionDto element) {    DAttr dAttr = editField.getDAttr();    String dAttrCode = dAttr == null ? null : dAttr.getCode();    if (DAttr.D_ATTR1_TYPE.equals(dattrType)) {      element.setDattr1(dAttrCode);    } else if (DAttr.D_ATTR2_TYPE.equals(dattrType)) {      element.setDattr2(dAttrCode);    } else if (DAttr.D_ATTR3_TYPE.equals(dattrType)) {      element.setDattr3(dAttrCode);    } else if (DAttr.D_ATTR4_TYPE.equals(dattrType)) {      element.setDattr4(dAttrCode);    } else if (DAttr.D_ATTR5_TYPE.equals(dattrType)) {      element.setDattr5(dAttrCode);    }  }}