package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.DAttrTreeSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.DAttr;import com.ufgov.zc.common.system.dto.ElementConditionDto;public class DAttrTreeSelectFieldConditionItem extends AbstractSearchConditionItem {  /**   *   */  private static final long serialVersionUID = 5456592838272815817L;  public DAttrTreeSelectFieldConditionItem(String displayName, String dattrType) {    this.dattrType = dattrType;    init(displayName);  }  public DAttrTreeSelectFieldConditionItem(String displayName, String dattrType, String compoId) {    this.dattrType = dattrType;    this.compoId = compoId;    init(displayName);  }  private DAttrTreeSelectField selectField;  private String dattrType;  private String compoId;  @Override  protected JComponent createEditorComponent() {    if (selectField == null) {      selectField = new DAttrTreeSelectField(20, dattrType, compoId);      selectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return selectField;  }  @Override  public void setValue(Object value) {    selectField.setDAttr((DAttr) value);  }  @Override  public Object getValue() {    return selectField.getDAttr();  }  public void putToElementConditionDto(ElementConditionDto element) {    DAttr dAttr = selectField.getDAttr();    String dAttrCode = dAttr == null ? null : dAttr.getCode();    if (DAttr.D_ATTR1_TYPE.equals(dattrType)) {      element.setDattr1(dAttrCode);    } else if (DAttr.D_ATTR2_TYPE.equals(dattrType)) {      element.setDattr2(dAttrCode);    } else if (DAttr.D_ATTR3_TYPE.equals(dattrType)) {      element.setDattr3(dAttrCode);    } else if (DAttr.D_ATTR4_TYPE.equals(dattrType)) {      element.setDattr4(dAttrCode);    } else if (DAttr.D_ATTR5_TYPE.equals(dattrType)) {      element.setDattr5(dAttrCode);    }  }}