package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.FundForBiXJTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Fund;public class FundForBiXJTableSelectFieldConditionItem extends AbstractSearchConditionItem {  public FundForBiXJTableSelectFieldConditionItem(String displayName) {    init(displayName);  }  private FundForBiXJTableSelectField manageTableSelectField;  @Override  protected JComponent createEditorComponent() {    if (manageTableSelectField == null) {      manageTableSelectField = new FundForBiXJTableSelectField(20);      manageTableSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return manageTableSelectField;  }  @Override  public void setValue(Object value) {    manageTableSelectField.setFund((Fund) value);  }  @Override  public Object getValue() {    return manageTableSelectField.getFund();  }}