package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.FundTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Fund;public class FundTableSelectFieldConditionItem extends AbstractSearchConditionItem {  private String compoId;  public FundTableSelectFieldConditionItem(String displayName) {    init(displayName);  }  public FundTableSelectFieldConditionItem(String displayName, String compoId) {    this.compoId = compoId;    init(displayName);  }  public FundTableSelectField fundTableSelectField;  @Override  protected JComponent createEditorComponent() {    if (fundTableSelectField == null) {      fundTableSelectField = new FundTableSelectField(18, compoId);      fundTableSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          Fund value = fundTableSelectField.getFund();          String code = null;          if (value != null) {            code = value.getCode();          }          fireSearch();          fireValueChanged();        }      });    }    return fundTableSelectField;  }  @Override  public void setValue(Object value) {    fundTableSelectField.setFund((Fund) value);  }  @Override  public Object getValue() {    return fundTableSelectField.getFund();  }}