package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.ManageComboBox;public class ManageComboboxConditionItem extends AbstractSearchConditionItem {  private String dataRuleId;  public ManageComboboxConditionItem(String displayName) {    init(displayName);  }  public ManageComboboxConditionItem(String displayName, String dataRuleId) {    this.dataRuleId = dataRuleId;    init(displayName);  }  private ManageComboBox manageComboBox;  @Override  protected JComponent createEditorComponent() {    if (manageComboBox == null) {      manageComboBox = new ManageComboBox(null, dataRuleId);      manageComboBox.initComboBox();      manageComboBox.addActionListener(new ActionListener() {        public void actionPerformed(ActionEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return manageComboBox;  }  @Override  public void setValue(Object value) {    manageComboBox.setSelectedItem(value);  }  @Override  public Object getValue() {    return manageComboBox.getSelectedItem();  }}