package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.OutlayTreeSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Outlay;public class OutlayTreeSelectFieldConditionItem extends AbstractSearchConditionItem {  public OutlayTreeSelectFieldConditionItem(String displayName) {    init(displayName);  }  private OutlayTreeSelectField outlayTreeSelectField;  @Override  protected JComponent createEditorComponent() {    if (outlayTreeSelectField == null) {      outlayTreeSelectField = new OutlayTreeSelectField(20);      outlayTreeSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          Outlay value = outlayTreeSelectField.getOutlay();          String code = null;          if (value != null) {            code = value.getCode();          }          fireSearch();          fireValueChanged();        }      });    }    return outlayTreeSelectField;  }  @Override  public void setValue(Object value) {    outlayTreeSelectField.setOutlay((Outlay) value);  }  @Override  public Object getValue() {    return outlayTreeSelectField.getOutlay();  }}