package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.PayoutComboBox;public class PayoutComboBoxConditionItem extends AbstractSearchConditionItem {  public PayoutComboBoxConditionItem(String displayName) {    init(displayName);  }  private PayoutComboBox payoutComboBox;  @Override  protected JComponent createEditorComponent() {    if (payoutComboBox == null) {      payoutComboBox = new PayoutComboBox();      payoutComboBox.initComboBox();      payoutComboBox.addActionListener(new ActionListener() {        public void actionPerformed(ActionEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return payoutComboBox;  }  @Override  public void setValue(Object value) {    payoutComboBox.setSelectedItem(value);  }  @Override  public Object getValue() {    return payoutComboBox.getSelectedItem();  }}