package com.ufgov.zc.client.component.ui.conditionitem;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.util.ArrayList;import java.util.List;import javax.swing.JComponent;import com.ufgov.zc.client.component.PlanTypeComboBox;public class PmAdjustComboboxConditionItem extends AbstractSearchConditionItem {  private List filterList = null;  public PmAdjustComboboxConditionItem(String displayName) {    init(displayName);  }  public PmAdjustComboboxConditionItem(String displayName, Object defaultValue) {    if (defaultValue != null) {      if (defaultValue instanceof ArrayList) {        ArrayList filterList = (ArrayList) defaultValue;        this.filterList = filterList;      }    }    init(displayName);  }  public PmAdjustComboboxConditionItem(String displayName, List filterList) {    this.filterList = filterList;    init(displayName);  }  private PlanTypeComboBox pmAdjustComboBox;  protected JComponent createEditorComponent() {    if (pmAdjustComboBox == null) {      if (filterList == null) {        pmAdjustComboBox = new PlanTypeComboBox();      } else {        pmAdjustComboBox = new PlanTypeComboBox(filterList);      }      pmAdjustComboBox.addActionListener(new ActionListener() {        public void actionPerformed(ActionEvent e) {          fireSearch();          fireValueChanged();        }      });    }    return pmAdjustComboBox;  }  public void setValue(Object value) {    pmAdjustComboBox.setSelectedItem(value);  }  @Override  public Object getValue() {    return pmAdjustComboBox.getSelectedItem();  }}