package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.ProjectBalanceTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Company;import com.ufgov.zc.common.commonbiz.model.Project;public class ProjectBalanceTableSelectFieldConditionItem extends AbstractSearchConditionItem {  public ProjectBalanceTableSelectFieldConditionItem(String displayName) {    init(displayName);  }  private ProjectBalanceTableSelectField projectBalanceTableSelectField;  protected JComponent createEditorComponent() {    if (projectBalanceTableSelectField == null) {      projectBalanceTableSelectField = new ProjectBalanceTableSelectField(20);      projectBalanceTableSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          Project value = projectBalanceTableSelectField.getProject();          String code = null;          if (value != null) {            code = value.getCode();          }          fireSearch();          fireValueChanged();        }      });    }    return projectBalanceTableSelectField;  }  public void setCompany(Company company) {    if (projectBalanceTableSelectField != null) {      projectBalanceTableSelectField.setCompany(company);    }  }  @Override  public void setValue(Object value) {    projectBalanceTableSelectField.setProject((Project) value);  }  @Override  public Object getValue() {    return projectBalanceTableSelectField.getProject();  }}