package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.ProjectTreeSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.Company;import com.ufgov.zc.common.commonbiz.model.Project;public class ProjectTreeFieldConditionItem extends AbstractSearchConditionItem {  private String compoId;  public ProjectTreeFieldConditionItem(String displayName) {    init(displayName);  }  public ProjectTreeFieldConditionItem(String displayName, String compoId) {    this.compoId = compoId;    init(displayName);  }  private ProjectTreeSelectField projectTreeSelectField;  protected JComponent createEditorComponent() {    if (projectTreeSelectField == null) {      projectTreeSelectField = new ProjectTreeSelectField(20, compoId);      projectTreeSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          Project value = projectTreeSelectField.getProject();          String code = null;          if (value != null) {            code = value.getCode();          }          fireSearch();          fireValueChanged();        }      });    }    return projectTreeSelectField;  }  public void setCompany(Company company) {    if (projectTreeSelectField != null) {      projectTreeSelectField.setCompany(company);    }  }  public void setValue(Object value) {    projectTreeSelectField.setProject((Project) value);  }  public Object getValue() {    return projectTreeSelectField.getProject();  }}