package com.ufgov.zc.client.component.ui.conditionitem;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.ProjectTypeTableSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.ProjectType;public class ProjectTypeTableSelectFieldConditionItem extends AbstractSearchConditionItem {  public ProjectTypeTableSelectFieldConditionItem(String displayName) {    init(displayName);  }  private ProjectTypeTableSelectField projectTypeTableSelectField;  protected JComponent createEditorComponent() {    if (projectTypeTableSelectField == null) {      projectTypeTableSelectField = new ProjectTypeTableSelectField(20);      projectTypeTableSelectField.addValueChangeListener(new ValueChangeListener() {        public void valueChanged(ValueChangeEvent e) {          ProjectType value = projectTypeTableSelectField.getProjectType();          String code = null;          if (value != null) {            code = value.getCode();          }          fireSearch();          fireValueChanged();        }      });    }    return projectTypeTableSelectField;  }  @Override  public void setValue(Object value) {    projectTypeTableSelectField.setProjectType((ProjectType) value);  }  @Override  public Object getValue() {    return projectTypeTableSelectField.getProjectType();  }}