package com.ufgov.zc.client.component.ui.fieldeditor;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JComponent;import com.ufgov.zc.client.component.BalModeComboBox;import com.ufgov.zc.common.commonbiz.aware.BalModeAware;import com.ufgov.zc.common.commonbiz.model.BalMode;public class BalModeComboBoxFieldEditor extends AbstractFieldEditor {  private BalModeComboBox balModeBox;  private String dataRuleId;  public BalModeComboBoxFieldEditor(String name) {    super(name);  }  public BalModeComboBoxFieldEditor(String name, boolean isEnabled) {    super(name);    balModeBox.setEnabled(isEnabled);  }  public BalModeComboBoxFieldEditor(String name, boolean isEnabled, String dataRuleId) {    this.dataRuleId = dataRuleId;    init(name);    balModeBox.setEnabled(isEnabled);  }  public Object getValue() {    return balModeBox.getSelectedItem();  }  public void setValue(Object value) {    if (value instanceof BalModeAware) {      BalModeAware balModeAware = (BalModeAware) value;      balModeBox.setSelectedBalModeByCode(balModeAware == null ? null : balModeAware.getBalModeCode());    }  }  protected JComponent createEditorComponent() {    balModeBox = new BalModeComboBox(dataRuleId);    balModeBox.addActionListener(new ActionListener() {      public void actionPerformed(ActionEvent e) {        if (getEditObject() instanceof BalModeAware) {          BalModeAware balModeAware = (BalModeAware) getEditObject();          if (balModeAware != null) {            BalMode balMode = balModeBox.getSelectedBalMode();            balModeAware.setBalModeCode(balMode == null ? null : balMode.getBalModeCode());          }          fireEditSynced();        }      }    });    return balModeBox;  }  @Override  public void setFieldEditorCode() {    this.FieldEditorCode = FieldEditorCodeConstants.BAL_MODE_CODE;  }}