package com.ufgov.zc.client.component.ui.fieldeditor;import javax.swing.JComponent;import com.ufgov.zc.client.component.element.CompanyTreeSelectField;import com.ufgov.zc.client.component.event.ValueChangeEvent;import com.ufgov.zc.client.component.event.ValueChangeListener;import com.ufgov.zc.common.commonbiz.model.BaseBill;import com.ufgov.zc.common.commonbiz.model.Company;public class CompanyFieldEditor extends AbstractFieldEditor {  private static final long serialVersionUID = -7677429808342983951L;  private CompanyTreeSelectField coField;  public CompanyFieldEditor(String name) {    super(name);  }  public CompanyFieldEditor(String name, boolean isEnabled) {    super(name);    coField.setEnabled(isEnabled);    coField.setEditable(isEnabled);  }  protected JComponent createEditorComponent() {    coField = new CompanyTreeSelectField();    coField.addValueChangeListener(new ValueChangeListener() {      public void valueChanged(ValueChangeEvent e) {        syncEditObject();      }    });    return coField;  }  /**   * 同步到对象   */  protected void syncEditObject() {    if (getEditObject() instanceof BaseBill) {      BaseBill bill = (BaseBill) getEditObject();      if (bill != null) {        Company company = coField.getCompany();        bill.setCoCode(company == null ? null : company.getCode());      }    }    this.fireEditSynced();  }  public Object getValue() {    return coField.getCompany();  }  public void setValue(Object value) {    if (value == null) {      coField.setValue(null);    } else if (value instanceof BaseBill) {      BaseBill bill = (BaseBill) value;      coField.setValueByCode(bill.getCoCode());    }  }  @Override  public void setFieldEditorCode() {    this.FieldEditorCode = FieldEditorCodeConstants.CO_CODE;  }}